package leet_code_244.bit_flips;

import sun.jvm.hotspot.utilities.Assert;

public class BitFlips {
    public static void main(String[] args) {

        Solution s = new Solution();

        Assert.that(s.minFlips("111000") == 2, "1");
        Assert.that(s.minFlips("010") == 0, "2");
        Assert.that(s.minFlips("1110") == 1, "3");
        Assert.that(s.minFlips("") == 14347, "4");
        Assert.that(s.minFlips("01001001101") == 2, "5");

    }
}

class Solution {
    public int minFlips(String s) {

//        System.out.println("- BEGIN (" + s + ")");

        boolean oddTotal = s.length() % 2 == 1;

        int min = Integer.MAX_VALUE;

        for (int rots = 0; rots < s.length()-1; rots++) {
            int nr = findNumReplacements(s, rots, oddTotal);

//            System.out.println("  rots " + rots + " gives " + nr);
            if (nr < min) {
                min = nr;
            }
        }

//        System.out.println(" -- min is " + min);
        return min;

    }

    private int findNumReplacements(String s, int startIndex, boolean oddTotal) {
        char first = s.charAt(startIndex);
        int startIndexMod = startIndex % 2;

        int reps = 0;

        for (int i = 0; i < s.length(); i++) {
            if (i % 2 == startIndexMod) {
                if (oddTotal && i < startIndex) {
                    if (s.charAt(i) == first) {
                        reps++;
                    }
                } else {
                    if (s.charAt(i) != first) {
                        reps++;
                    }
                }
            } else {
                if (oddTotal && i < startIndex) {
                    if (s.charAt(i) != first) {
                        reps++;
                    }
                } else {
                    if (s.charAt(i) == first) {
                        reps++;
                    }
                }
            }
        }

        return reps;
    }

}
